import { DataType, TimeAgoType } from "./types";

export interface ChildProps {
  children?: React.ReactNode;
}

export function areEqual(array1: DataType[], array2: DataType[]) {
  if (array1.length === array2.length) {
    return array1.every((element, index) => {
      if (element === array2[index]) {
        return true;
      }

      return false;
    });
  }

  return false;
}

export function timeAgo(input: string) {
  const date = new Date(input);
  const formatter = new Intl.RelativeTimeFormat("en");
  const ranges = {
    years: 3600 * 24 * 365,
    months: 3600 * 24 * 30,
    weeks: 3600 * 24 * 7,
    days: 3600 * 24,
    hours: 3600,
    minutes: 60,
    seconds: 1,
  };
  const secondsElapsed = (date.getTime() - Date.now()) / 1000;
  for (let key in ranges) {
    const tempKey = key as keyof TimeAgoType;
    const temp = ranges[tempKey];
    if (temp < Math.abs(secondsElapsed)) {
      const delta = secondsElapsed / temp;
      return formatter.format(Math.round(delta), tempKey);
    }
  }
}

